#include <stdio.h>
int data[101];
int dy[51][101];
 int diff(int m,int n)
{
   int i,max,min;
    max = min = data[m];
    for( i = m +1 ; i <= n ; i++){
      if ( max < data[i] ) max = data[i];
      if ( min > data[i] ) min = data[i];
   }
    return max - min;
}
 int main()
{
   int i,j,k;
   int n,ng,max,tmp;
    scanf("%d %d",&n,&ng);
    for( i = 1 ; i <= n ; i++){
      scanf("%d",&data[i]);
   }
    for( i = 1 ; i <= ng ; i++){
      for( j = i*2 ; j <= n ; j++){
         max = -1;
         for( k = 2*(i-1) ; k <= j-2 ; k++){
            tmp = diff(k+1,j);
            if ( dy[i-1][k] + tmp> max ) {
               max = dy[i-1][k] + tmp;
            }
         }
         dy[i][j] = max;
      }
   }
    int ans=-1;
    for( i = ng*2 ; i <= n ; i++){
      if ( dy[ng][i] > ans ) ans = dy[ng][i];
   }
    printf("%d\n",ans);
}

